mapper.select:

SELECT COUNT(*) AS same_room_cnt
FROM chat_room_user u1
JOIN chat_room_user u2
  ON u1.chat_room_id = u2.chat_room_id
WHERE u1.emp_id = #{chatTargetId}
  AND u2.emp_id = #{username}
  AND u1.chat_room_is_active = 1 AND u1.exited_at IS NULL
  AND u2.chat_room_is_active = 1 AND u2.exited_at IS NULL;
