mapper.select:
SELECT ad.approval_document_id AS approvalDocumentId, ad.approval_title AS title, ad.document_type AS documentType, ad.approval_status AS status, ad.create_at AS createdAt, ad.complete_at AS completeAt FROM approval_document ad WHERE ad.emp_id = #{empId}
AND ad.approval_status = #{status}
ORDER BY ad.approval_document_id DESC

mapper.update:
UPDATE approval_line
SET approval_status = #{status},
    approve_at = NOW()
WHERE approval_line_id = #{approvalLineId}
  AND emp_id = #{approverEmpId}
  AND approval_status = 'PENDING'

UPDATE annual_leave
SET annual_leave_count = COALESCE(annual_leave_count, 0) - #{days}
WHERE emp_id = #{empId}

UPDATE approval_document
SET approval_title = #{title},
    is_file = #{isFile}
WHERE approval_document_id = #{approvalDocumentId}

mapper.delete:
DELETE FROM file_resource
WHERE file_ref_type = 'APPROVAL'
  AND file_ref_id   = #{approvalDocumentId}

mapper.select:
SELECT emp_id AS empId, approval_status AS status
FROM approval_document
WHERE approval_document_id = #{approvalDocumentId}
